mapper.select:
select
  id, title, sub_title, description, file_type, file_path, user_id, create_time, is_del, is_lock
from article


        and id = #{id}


        and title = #{title}


        and sub_title = #{subTitle}


        and description = #{description}


        and file_type = #{fileType}


        and file_path = #{filePath}


        and user_id = #{userId}


        and create_time = #{createTime}


        and is_del = #{isDel}


        and is_lock = #{isLock}
